$server = $env:computername;$disksResult = '';$cpuResult = '';$ramResult = '';$drives=Get-WmiObject Win32_LogicalDisk -ComputerName $server;foreach ($drive in $drives){$drivename=$drive.DeviceID -split ':';    if($drivename -eq 'C'){        $freespace=[int]($drive.FreeSpace/1GB);        $disksResultForParsing = $freespace;        break;    }} $cpu=Get-WmiObject Win32_Processor -ComputerName $server; $cpuResult = $cpu.LoadPercentage; $ComputerMemory = Get-WmiObject -ComputerName $server -Class win32_operatingsystem -ErrorAction Stop;$Memory = ((($ComputerMemory.TotalVisibleMemorySize - $ComputerMemory.FreePhysicalMemory)*100)/ $ComputerMemory.TotalVisibleMemorySize);$ramResult = [math]::Round($Memory, 0);$outputForParsing = $cpuResult.ToString()+','+$ramResult.ToString()+','+$disksResultForParsing.ToString();$outputForParsing;